import React from 'react';
import { Modal, Descriptions, Image, Tag, Row, Col, Typography, Divider } from 'antd';
import moment from 'moment';

const { Title, Paragraph } = Typography;

const BlogDetailsModal = ({ visible, onClose, blog }) => {
    if (!blog) {
        return null;
    }

    // Format created date
    const formatDate = (dateArray) => {
        if (!dateArray) return '';
        const [year, month, day, hour, minute, second] = dateArray;
        return moment(new Date(year, month - 1, day, hour, minute, second)).format('YYYY-MM-DD HH:mm:ss');
    };

    // Format paragraphs for better display
    const formatContent = (content) => {
        if (!content) return [];
        return content.split('\n').filter(paragraph => paragraph.trim().length > 0);
    };

    const formattedParagraphs = formatContent(blog.content);

    return (
        <Modal
            title={<Title level={3}>{blog.title}</Title>}
            visible={visible}
            onCancel={onClose}
            footer={null}
            width={800}
        >
            <Descriptions bordered column={1}>
                <Descriptions.Item label="Author">{blog.authorName}</Descriptions.Item>
                <Descriptions.Item label="Category">
                    <Tag color="blue">{blog.category}</Tag>
                </Descriptions.Item>
                <Descriptions.Item label="Tags">
                    <Tag color="green">{blog.tags}</Tag>
                </Descriptions.Item>
                <Descriptions.Item label="Created At">{formatDate(blog.createdAt)}</Descriptions.Item>
                <Descriptions.Item label="Published Status">
                    {blog.isPublished ?
                        <Tag color="success">Published</Tag> :
                        <Tag color="warning">Draft</Tag>
                    }
                </Descriptions.Item>
                <Descriptions.Item label="View Count">{blog.viewCount}</Descriptions.Item>
                <Descriptions.Item label="Likes">{blog.likesCount}</Descriptions.Item>
            </Descriptions>

            <Divider orientation="left">Content</Divider>
            <Typography>
                {formattedParagraphs.map((paragraph, index) => (
                    <Paragraph key={index}>{paragraph}</Paragraph>
                ))}
            </Typography>

            <Divider orientation="left">Images</Divider>
            {blog.thumbnailUrl && blog.thumbnailUrl.length > 0 ? (
                <Row gutter={[16, 16]}>
                    {blog.thumbnailUrl.map((image) => (
                        <Col key={image.id} xs={24} sm={12} md={8} lg={6}>
                            <Image
                                src={image.imageUrl}
                                alt={`Image ${image.id}`}
                                style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                                fallback="data:image/png;base64,"
                            />
                        </Col>
                    ))}
                </Row>
            ) : (
                <Typography.Text type="secondary">No images available</Typography.Text>
            )}

            {blog.comments && blog.comments.length > 0 && (
                <>
                    <Divider orientation="left">Comments ({blog.comments.length})</Divider>
                    {/* Comments display would go here */}
                </>
            )}
        </Modal>
    );
};

export default BlogDetailsModal;